mapper.select:
SELECT *
FROM notice_comment
WHERE ncseq = #{ncseq}
	AND nseq = #{nseq}

SELECT COUNT(*)
FROM notice_comment
WHERE nseq = #{nseq}


SELECT ncseq, nseq, content, writer, regdate, modifydate
FROM (SELECT ROW_NUMBER() OVER(ORDER BY regdate) rn, ncseq, nseq, content, writer, regdate, modifydate
	FROM notice_comment
	WHERE nseq = #{nseq})
WHERE rn <= #{criteria.pageNum} * #{criteria.rowsPerPage}
	AND rn > (#{criteria.pageNum} - 1) * #{criteria.rowsPerPage}

mapper.update:
UPDATE notice_comment
SET content = #{content}, modifyDate = SYSDATE
WHERE ncseq = #{ncseq}
	AND nseq = #{nseq}

mapper.delete:
DELETE
FROM notice_comment
WHERE ncseq = #{ncseq}
	AND nseq = #{nseq}
