mapper.select:
SELECT COUNT(*)
FROM notice_comment
WHERE nseq=#{nseq}


SELECT notice_comment_seq, nseq, content, writer, regdate, modifydate
FROM (SELECT ROW_NUMBER() OVER(ORDER BY regdate) rn, notice_comment_seq, nseq, content, writer, regdate, modifydate
	FROM notice_comment
	WHERE nseq=#{nseq})
WHERE rn <= #{criteria.pageNum} * #{criteria.rowsPerPage}
	AND rn > (#{criteria.pageNum} - 1) * #{criteria.rowsPerPage}
