mapper.select:
SELECT COUNT(*)
FROM product_comment
WHERE pseq=#{pseq}


SELECT comment_seq, pseq, content, writer, regdate, modifydate
FROM (SELECT ROW_NUMBER() OVER(ORDER BY regdate) rn, comment_seq, pseq, content, writer, regdate, modifydate
	FROM product_comment
	WHERE pseq=#{pseq})
WHERE rn <= #{criteria.pageNum} * #{criteria.rowsPerPage}
	AND rn > (#{criteria.pageNum} - 1) * #{criteria.rowsPerPage}

mapper.update:
UPDATE product_comment
SET content=#{content}, modifyDate=SYSDATE
WHERE comment_seq=#{comment_seq}
